/**
 * Audio
 * @class Audio
 * @export
 */
class Audio {

  /**
   * @constructor
   */
  constructor() {

    /**
     * Audio res path
     * @type {String}
     */
    this.path = "assets/audio/";

    /**
     * Noises
     * @type {Array}
     */
    this.noises = [];

  }

  /**
   * Play a sound
   * @param {String} name
   * @param {Number} vol
   * @param {Number} x
   * @param {Number} y
   */
  playSound(name, vol, x, y) {
    let path = this.path + `${name}.ogg`;
    var sound = new Howl({
      urls: [path],
      autoplay: true,
      loop: false,
      pos3d: [x, y, vol / 1e3]
    });
  }

  /**
   * Play a song
   * @param {String}  name
   * @param {Number}  vol
   * @param {Boolean} fadeIn
   */
  playSong(name, vol, fadeIn) {
    vol = vol / 1e2;
    let path = this.path + `${name}.ogg`;
    var song = new Howl({
      urls: [path],
      autoplay: true,
      loop: true,
      volume: fadeIn ? 0 : vol
    });
    if (fadeIn) {
      song.fadeIn(vol, 2e3);
    }
  }

  /**
   * Play a noise
   * @param  {String} name
   * @param  {Number} vol
   * @param  {Number} x
   * @param  {Number} y
   * @return {Object}
   */
  playNoise(name, vol, x, y) {
    let path = this.path + `${name}.ogg`;
    var noise = new Howl({
      urls: [path],
      autoplay: true,
      loop: true,
      volume: vol / 1e2,
      pos3d: [x, y, vol / 1e3]
    });
    this.noises.push(noise);
    /** This is for smooth out/in fading noise range area */
    noise.isInView = true;
    noise.fadingIn = false;
    noise.fadingOut = false;
    return (noise);
  }

}

export default Audio = new Audio();

import {
  DEBUG_FPS,
  FREE_CAMERA,
  EASING_CAMERA,
  DIMENSION,
  MIN_SCALE, MAX_SCALE,
  PIXEL_SCALE
} from "../../cfg";

import math from "../../Math";

import DisplayObject from "../DisplayObject";

/**
 * Camera
 * @class Camera
 * @export
 */
export default class Camera extends DisplayObject {

  /**
   * @constructor
   * @param {Object} instance
   */
  constructor(instance) {

    super(null);

    /**
     * Instance ref
     * @type {Object}
     */
    this.instance = instance;

    /**
     * Camera size
     * @type {Number}
     */
    this.width = instance.width;
    this.height = instance.height;

    /**
     * Drag offset
     * @type {Object}
     */
    this.drag = {
      px: 0,
      py: 0,
      pz: 0,
      sx: 0,
      sy: 0
    };

    /**
     * Dragging state
     * @type {Boolean}
     */
    this.dragging = false;

    /** Camera size */
    this.size.set(
      this.width  || 0,
      this.height || 0
    );

    /**
     * Camera scaling
     * @type {Number}
     */
    this.scaling = .0;

    /**
     * Camera calculated resolution
     * @type {Number}
     */
    this.resolution = .0;

    /**
     * Base offset
     * @type {Object}
     */
    this.base = new math.Point(.0, .0);

    /**
     * Target offset
     * @type {Object}
     */
    this.target = new math.Point(.0, .0);

    /**
     * Object to focus
     * @type {Object}
     */
    this.objectFocus = null;

    /**
     * Scale
     * @type {Number}
     * @getter
     * @setter
     */
    Object.defineProperty(this, "scale", {
      get: function() {
        return (this.scaling);
      },
      set: function(value) {
        this.scaling = value;
        this.refreshResolution();
      }
    });

  }

  /**
   * Get game relative mouse offset
   * @param  {Number} x clientX
   * @param  {Number} y clientY
   * @return {Object}
   */
  getGameMouseOffset(x, y) {

    let xx = ((x - this.x) / this.resolution);
    let yy = ((y - this.y) / this.resolution);

    return ({
      x: ((Math.ceil(xx / DIMENSION) * DIMENSION) - DIMENSION) << 0,
      y: ((Math.ceil(yy / DIMENSION) * DIMENSION) - DIMENSION) << 0
    });

  }

  /**
   * Move
   * @param {Number} x
   * @param {Number} y
   */
  move(x, y) {

    this.x += x - this.drag.px;
    this.y += y - this.drag.py;

    this.drag.px = x;
    this.drag.py = y;

  }

  /**
   * Click
   * @param {Number} x
   * @param {Number} y
   */
  click(x, y) {

    this.drag.sx = (x - this.x) / this.resolution;
    this.drag.sy = (y - this.y) / this.resolution;

    this.drag.px = x;
    this.drag.py = y;

  }

  /**
   * Refresh the resolution
   */
  refreshResolution() {
    this.resolution = math.roundTo(parseFloat(math.zoomScale(this.scale)), PIXEL_SCALE);
  }

  /**
   * Zoom
   * @param {Object} e
   */
  zoom(e) {

    let delta = e.deltaY === -0 ? e.deltaX : e.deltaY;

    let amount = (delta > 0 ? -100 : 100);

    amount = amount / 2 / (math.hypot(this.size.x, this.size.y) / Math.PI) * math.zoomScale(this.scale);

    this.drag.pz = this.resolution;

    this.scale += amount / 2;

    if (this.scale < MIN_SCALE) this.scale = MIN_SCALE;
    if (this.scale > MAX_SCALE) this.scale = MAX_SCALE;

    let focus = this.objectFocus;

    if (FREE_CAMERA === true) {
      this.position.x -= (this.drag.sx) * (math.zoomScale(this.resolution) - math.zoomScale(this.drag.pz));
      this.position.y -= (this.drag.sy) * (math.zoomScale(this.resolution) - math.zoomScale(this.drag.pz));
    } else {
      if (focus !== null) {
        this.position.x -= (focus.position.x + (focus.size.x * focus.scale / 2) + focus.xMargin) * (math.zoomScale(this.resolution) - math.zoomScale(this.drag.pz));
        this.position.y -= (focus.position.y + ((focus.size.y * focus.scale / 2) + focus.yMargin + focus.z)) * (math.zoomScale(this.resolution) - math.zoomScale(this.drag.pz));
      }
    }

  }

  /**
   * Get x center position
   * @param  {Object} object
   * @return {Number}
   */
  getX(object) {
    return (
      this.size.x / 2 - ((object.position.x + (object.size.x * object.scale / 2) + object.xMargin) * this.resolution)
    );
  }

  /**
   * Get y center position
   * @param  {Object} object
   * @return {Number}
   */
  getY(object) {
    return (
      this.size.y / 2 - ((object.position.y + ((object.size.y * object.scale / 2) + object.yMargin + object.z)) * this.resolution)
    );
  }

  /**
   * Update object focus
   * @param  {Number} object
   */
  updateFocus(object) {

    this.base = {
      x: this.position.x,
      y: this.position.y
    };

    this.target = {
      x: this.getX(object),
      y: this.getY(object)
    };

    this.deltaX = this.target.x - this.base.x;
    this.deltaY = this.target.y - this.base.y;

    return void 0;

  }

  /**
   * Play camera animations
   * @param {Object} object
   */
  animate(object) {

    if (FREE_CAMERA === true) return void 0;

    this.updateFocus(object);

    let velocity = EASING_CAMERA === true ? 0 : math.ease(Math.atan(DEBUG_FPS / 60 + .05));

    let x = Math.round((this.target.x - (this.base.x + (velocity * this.deltaX))) * 1e2) / 1e2;
    let y = Math.round((this.target.y - (this.base.y + (velocity * this.deltaY))) * 1e2) / 1e2;

    if (x !== -0 && x !== 0) {
      this.position.x += x;
    }

    if (y !== -0 && y !== 0) {
      this.position.y += y;
    }

    return void 0;

  }

  /**
   * Animate focus
   * @param {Object} object
   */
  animateFocus(object) {
    this.updateFocus(object);
    this.objectFocus = object;
  }

  /**
   * Focus a object
   * @param {Object}  object
   * @param {Boolean} instant
   */
  focus(object, instant) {
    if (
      object === null ||
      object === void 0 ||
      object === -1
    ) return void 0;
    if (instant === true) {
      this.objectFocus = object;
      this.position.x = this.getX(object);
      this.position.y = this.getY(object);
      return void 0;
    }
    this.animateFocus(object);
  }

  /**
   * Cubic in view
   * @param {Number} x
   * @param {Number} y
   * @param {Number} width
   * @param {Number} height
   * @return {Boolean}
   */
  inView(x, y, width, height) {

    return (
      x + width  >= 0 && x <= this.size.x &&
      y + height >= 0 && y <= this.size.y
    );

  }

  /**
   * Is in view
   * @param {Number} x
   * @param {Number} y
   * @param {Number} width
   * @param {Number} height
   */
  isInView(x, y, width, height) {

    return (
      this.inView(
        ((x * this.resolution) + this.position.x) << 0,
        ((y * this.resolution) + this.position.y) << 0,
        width * this.resolution << 0,
        height * this.resolution << 0
      )
    );

  }

}

/**
 * Commander
 * @class Commander
 * @export
 */
export default class Commander {

  /**
   * @constructor
   */
  constructor() {

    /**
     * Stack position
     * @type {Number}
     */
    this.position = -1;

    /**
     * Command templates
     * @type {Object}
     */
    this.commands = {};

    /**
     * Command stack
     * @type {Array}
     */
    this.stack = [];

  }

  /**
   * Register a new command
   * @param {Object} cmd
   */
  newCommand(cmd) {
    this.commands[cmd.action] = cmd;
    cmd = null;
  }

  /**
   * Push a command
   * @param {String} action
   * @param {Object} scope
   * @param {Array} data
   */
  push(action, scope, data) {

    let cmd = {
      action: action,
      data:   data,
      scope:  scope
    };

    this.stack.splice(this.position + 1, this.stack.length);

    this.stack.push(cmd);

    this.redo();
    this.undo();
    this.redo();
    this.undo();
    this.redo();

  }

  /**
   * Fire command
   * @param {Object} cmd
   * @param {String} action
   */
  fire(cmd, action) {
    let template = this.commands[cmd.action][action];
    template.bind(cmd.scope).apply(template, cmd.data);
  }

  /**
   * Get cmd from current stack index
   * @return {Object}
   */
  getCurrentCmd() {
    return (this.stack[this.position]);
  }

  /**
   * Undo
   */
  undo() {

    if (this.position >= 0) {
      this.fire(this.getCurrentCmd(), "onUndo");
      this.position--;
    }

  }

  /**
   * Redo
   */
  redo() {

    if (this.position < this.stack.length - 1) {
      this.position++;
      this.fire(this.getCurrentCmd(), "onRedo");
    }

  }

}

import * as Packet from "../../Packets";

import * as cfg from "../../cfg";

import {
  Maps
} from "../utils";

import math from "../../Math";

/**
 * Connection
 * @class Connection
 * @export
 */
export default class Connection {

  /**
   * @constructor
   * @param {Object} instance
   * @param {String} url
   */
  constructor(instance, url) {

    /**
     * Instance ref
     * @type {Object}
     */
    this.instance = instance;

    /**
     * Websocket instance
     * @type {Object}
     */
    this.ws = null;

    /**
     * Opening state
     * @type {Boolean}
     */
    this.open = false;

    this.init(url);

  }

  /**
   * Initialise a ws connection
   * @param {String} url
   */
  init(url) {

    this.open = true;

    this.ws = new window.WebSocket(`ws://${url}`);

    this.ws.binaryType = "arraybuffer";

    this.ws.addEventListener('open',    this::this.onOpen);
    this.ws.addEventListener('close',   this::this.onClose);
    this.ws.addEventListener('error',   this::this.onError);
    this.ws.addEventListener('message', this::this.onMessage);

  }

  /**
   * On open
   * @param {Object} e
   */
  onOpen(e) {

    console.log("Socket Open!");

    this.sendUserData();

  }

  /**
   * On close
   * @param {Object} e
   */
  onClose(e) {
    console.log(e);
  }

  /**
   * On error
   * @param {Object} e
   */
  onError(e) {
    console.log("Socket closed!");
  }

  /**
   * On message
   * @param {Object} e
   */
  onMessage(e) {
    /*let delta = this.instance.engine.renderer.delta * 1e3;
    setTimeout(this::function() {
      this.handleMessage(new DataView(e.data));
    }, delta);*/
    this.handleMessage(new DataView(e.data));
  }

  /**
   * Handle a message
   * @param {Object} msg
   */
  handleMessage(msg) {

    let offset = 0;

    let key = msg.getUint8(offset++);

    function getString() {
      var text = "";
      var char = 0;
      while ((char = msg.getUint16(offset, true)) !== 0) {
        offset += 2;
        text += String.fromCharCode(char);
      };
      offset += 2;
      return (text);
    }

    if (key === 0) {
      let data = this.getString(msg);
      console.log(data);
    }

    /** User joined map */
    if (key === 22) {
      offset += 4;
      offset += 4;
      let data = JSON.parse(getString());
      let player = this.instance.entities.Player;
      let entity = new player(data);
      entity.instance = this.instance.engine;
      this.instance.engine.addEntity(entity);
      if (entity.isLocalPlayer === true) {
        this.instance.engine.localEntity = entity;
        this.instance.engine.camera.focus(entity, false);
      }
    }

    /** Nerby players */
    if (key === 40) {
      offset += 4;
      offset += 4;
      let data = JSON.parse(getString());
      for (let key in data) {
        this.instance.engine.addEntity(
          new this.instance.entities.Player(data[key])
        );
      };
    }

    /** Jumping */
    if (key === 30) {
      offset += 4;
      offset += 4;
      let data = JSON.parse(getString());
      let entity = this.instance.engine.getEntityByProperty(data.name, "name");
      entity.jump();
    }

    /** Facing */
    if (key === 31) {
      offset += 4;
      offset += 4;
      let data = JSON.parse(getString());
      let entity = this.instance.engine.getEntityByProperty(data.name, "name");
      entity.changeFacing(data.dir);
    }

    /** Movement */
    if (key === 32) {
      offset += 4;
      offset += 4;
      let data = JSON.parse(getString());
      let entity = this.instance.engine.getEntityByProperty(data.name, "name");
      let position = math.getTilePosition(data.x, data.y, data.dir);
      let obstacle = Maps[entity.map].isEntityCollidable(entity, position.x, position.y) === true;
      entity.onlineMove(position.x, position.y, position.facing);
    }

    /** Velocity */
    if (key === 33) {
      offset += 4;
      offset += 4;
      let data = JSON.parse(getString());
      let entity = this.instance.engine.getEntityByProperty(data.name, "name");
      entity.velocity = data.velocity;
    }

    /** Kill */
    if (key === 34) {
      offset += 4;
      offset += 4;
      let data = JSON.parse(getString());
      let entity = this.instance.engine.getEntityByProperty(data.name, "name");
      entity.fadeOut(1, true);
    }

    return void 0;

  }

  /**
   * Prepare data to send
   * @param  {Number} length
   * @return {Object}
   */
  prepareData(length) {
    return (
      new DataView(new ArrayBuffer(length))
    );
  }

  /**
   * Send data
   * @param {Object} a
   */
  send(a) {
    this.ws.send(a.buffer);
    return void 0;
  }

  /**
   * Send user data
   */
  sendUserData() {

    if (this.open === false) return void 0;

    let name = cfg.LOCAL_PLAYER;

    let msg = this.prepareData(1 + 2 * name.length);

    msg.setUint8(0, 0);

    for (var ii = 0; ii < name.length; ++ii) {
      msg.setUint16(1 + 2 * ii, name.charCodeAt(ii), true);
    };

    this.send(msg);

  }

  /**
   * Sto buffer
   * @param {Array} message
   * @return {Object}
   */
  stobuf(buffer) {

    let ii = 0;
    let length = buffer.length;
    let arrayBuffer = new ArrayBuffer(length);
    let view = new Uint8Array(arrayBuffer);

    for (; ii < length; ++ii) {
      view[ii] = buffer[ii];
    };

    return (view.buffer);
  }

  /**
   * Send data
   * @param {String} type
   * @param {Array}  data
   */
  sendData(type, data) {

    let pClass = Packet[type];

    if (
      pClass === void 0 ||
      pClass === null
    ) return void 0;

    let packet = new pClass(...data);

    this.send(packet);

  }

  getString(view) {

    if ((view.byteLength + 1) % 2 === 1) {
      return void 0;
    }

    var txt = "";
    var maxLen = 32 * 2;
    for (var i = 1; i < view.byteLength && i <= maxLen; i += 2) {
      var charCode = view.getUint16(i, true);
      if (charCode == 0) {
        return void 0;
      }
      txt += String.fromCharCode(charCode);
    }
    return (txt);
  }

}

import * as cfg from "../../cfg";

export let actions = {};

actions["SHIFT"] = {
  action: function() {
    this.engine.editor.STATES.SELECTING = false;
    this.engine.editor.selectedEntities = [];
  },
  rule: () => cfg.EDIT_MODE
};

actions["CTRL+Z"] = {
  action: function() {
    this.engine.editor.commander.undo();
  },
  rule: () => cfg.EDIT_MODE
};

actions["CTRL+Y"] = {
  action: function() {
    this.engine.editor.commander.redo();
  },
  rule: () => cfg.EDIT_MODE
};

actions["CTRL+C"] = {
  action: function() {
    this.engine.editor.copyEntity();
  },
  rule: () => cfg.EDIT_MODE
};

actions["CTRL+V"] = {
  action: function() {
    this.engine.editor.pasteEntity();
  },
  rule: () => cfg.EDIT_MODE
};

actions["CTRL+X"] = {
  action: function() {
    this.engine.editor.cutEntity();
  },
  rule: () => cfg.EDIT_MODE
};

actions["DELETE"] = {
  action: function() {
    this.engine.editor.deleteEntity();
  },
  rule: () => cfg.EDIT_MODE
};

actions["F1"] = {
  action: function() {
    cfg.DEBUG_MODE = cfg.DEBUG_MODE ? false : true;
    /*if (!cfg.DEBUG_MODE) {
      cfg.FREE_CAMERA = false;
      this.engine.camera.focus(this.engine.localEntity, false);
    }*/
    this.engine.renderer.switchRenderingMode(cfg.DEBUG_MODE ? 0 : 1);
    this.engine.renderer.resize(true);
    this.engine.renderer.clear();
    this.engine.renderer.draw();
  },
  rule: () => true
};

actions["F2"] = {
  action: function() {
    cfg.EDIT_MODE = cfg.EDIT_MODE ? false : true;
  },
  rule: () => cfg.DEBUG_MODE
};

actions["F3"] = {
  action: function() {
    cfg.FREE_CAMERA = cfg.FREE_CAMERA ? false : true;
    if (!cfg.FREE_CAMERA) {
      this.engine.camera.dragging = false;
    }
  },
  rule: () => cfg.DEBUG_MODE
};

actions["F4"] = {
  action: function() {
    cfg.GOD_MODE = cfg.GOD_MODE ? false : true;
  },
  rule: () => cfg.DEBUG_MODE
};

actions["F6"] = {
  action: function() {
    cfg.MINI_MAP = cfg.MINI_MAP ? false : true;
    cfg.TILESET_MODE = !cfg.MINI_MAP;
  },
  rule: () => cfg.DEBUG_MODE
};

actions["B"] = {
  action: function() {
    this.engine.notify(this.engine.localEntity, "Hello World");
  },
  rule: () => true
};

actions["Z"] = {
  action: function() {
    let local = this.engine.localEntity;
    local.action();
  },
  rule: function() { return (this.engine.localEntity !== null); }
};

actions["X_FIRE"] = {
  action: function() {
    let local = this.engine.localEntity;
    local.velocity = 2;
  },
  rule: function() { return (this.engine.localEntity !== null); }
};

actions["X_LEAVE"] = {
  action: function() {
    let local = this.engine.localEntity;
    local.velocity = 1;
  },
  rule: function() { return (this.engine.localEntity !== null); }
};

actions["C"] = {
  action: function() {
    let local = this.engine.localEntity;
    local.jump();
  },
  rule: function() { return (this.engine.localEntity !== null); }
};

actions["←"] = {
  action: function() {
    let local = this.engine.localEntity;
    local.move(cfg.LEFT);
  },
  rule: function() { return (this.engine.localEntity !== null); }
};

actions["→"] = {
  action: function() {
    let local = this.engine.localEntity;
    local.move(cfg.RIGHT);
  },
  rule: function() { return (this.engine.localEntity !== null); }
};

actions["↑"] = {
  action: function() {
    let local = this.engine.localEntity;
    local.move(cfg.UP);
  },
  rule: function() { return (this.engine.localEntity !== null); }
};

actions["↓"] = {
  action: function() {
    let local = this.engine.localEntity;
    local.move(cfg.DOWN);
  },
  rule: function() { return (this.engine.localEntity !== null); }
};

actions["SPACE"] = {
  action: function() {
    let local = this.engine.localEntity;
    this.engine.camera.focus(local, true);
    cfg.FREE_CAMERA = false;
  },
  rule: function() {
    return (
      cfg.FREE_CAMERA ||
      this.engine.camera.objectFocus !== null &&
      this.engine.camera.objectFocus.id !== this.engine.localEntity.id
    );
  },
  log: false
};

actions["DBLCLICK"] = {
  action: function(e) {
    e.preventDefault();
    if (!cfg.DEBUG_MODE) return void 0;
    cfg.FREE_CAMERA = false;
    if (cfg.EDIT_MODE) {
      if (cfg.TILESET_MODE === true && this.engine.editor.clickedInsideTileset(x, y)) {
        return void 0;
      }
      if (!this.engine.editor.dragging) {
        let entity = this.engine.editor.getEntityByMouse(e.clientX, e.clientY);
        if (entity !== null) {
          this.engine.camera.focus(entity, false);
        }
        this.engine.editor.editEntity(e.clientX, e.clientY);
      }
    }
  },
  rule: () => true,
  log: false
};

actions["LEFTCLICK"] = {
  action: function(e) {
    if (
      e.target.id !== this.engine.node.id &&
      e.target.id !== this.engine.glNode.id
    ) {
      return void 0;
    }
    let x = e.touches ? e.touches[0].clientX : e.clientX;
    let y = e.touches ? e.touches[0].clientY : e.clientY;
    e.preventDefault();
    if (cfg.TILESET_MODE === true && this.engine.editor.clickedInsideTileset(x, y)) {
      this.engine.editor.selectTile(x, y);
      return void 0;
    }
    if (this.input.KeyBoard.isKeyPressed("G")) {
      this.engine.ping(x, y, "notify");
      return void 0;
    }
    //if (!cfg.DEBUG_MODE) return void 0;
    if (e.which === 1 && this.input.KeyBoard.isKeyPressed("SHIFT")) {
      this.engine.editor.STATES.SELECTING = true;
      this.engine.editor.selectFrom(x, y);
      this.engine.editor.selectTo(x, y);
      return void 0;
    }
    if (e.which !== 1 && !this.input.KeyBoard.isKeyPressed("SPACE")) {
      cfg.FREE_CAMERA = true;
    }
    if (cfg.FREE_CAMERA && (e.touches || e.which !== 1)) {
