    if (rule === true) {
      cmd.action.bind(this.engine.instance).apply(this, args);
    }

    if (cmd.log !== false) this.log(name, rule);

    return void 0;

  }

  /**
   * Log a action
   * @param {String} name
   */
  log(name, failed) {

    this.logs.push({
      name: name,
      success: failed,
      timestamp: this.instance.renderer.now
    });

  }

}

