/**
 * Parse arguments
 * [ , ]
 * @return {Array}
 */
export function parseArguments() {

  let args = [];

  let tmp = null;

  this.expect("LPAREN");

  tmp = this.parseBlock();

  if (tmp !== null) {
    args.push(tmp);
  }

  for (;this.accept("COMMA") === true;) {
    this.next();
    if (this.accept("LPAREN") === true) {
      this.next();
      tmp = this.parseCallExpression();
      if (tmp !== null) {
        args.push(tmp);
      }
    } else {
      tmp = this.parseBlock();
      if (tmp !== null) {
        args.push(tmp);
      }
    }
    if (this.accept("RPAREN") === true) {
      this.next();
      break;
    }
  };

  if (args.length <= 1 && this.accept("RPAREN") === true) {
    this.next();
  }

  return (args);

}

/**
 * Parse brace body
 * { Body }
 * @return {Object}
 */
export function parseBraceBody() {

  let ast = null;

  this.expect("LBRACE");
  ast = this.parseBlockStatement();
  this.expect("RBRACE");

  return (ast);

}

/**
 * Parse parenthese expression
 * ( Expression )
 */
export function parseParentheseExpression() {

  let ast = null;

  this.expect("LPAREN");
  ast = this.parseExpression(0);
  this.expect("RPAREN");

  return (ast);

}

