        this.glNode.style.display = "block";
        cfg.RENDER_MODE = mode;
      } else {
        mode = cfg.CANVAS;
      }
    }

    if (mode === cfg.CANVAS) {
      this.node.style.display = "block";
      this.glNode.style.display = "none";
      cfg.RENDER_MODE = mode;
    }

  }

