/**
 * Shadow
 * @class Shadow
 * @export
 */
export default class Shadow extends DisplayObject {

  /**
   * @param {Object} parent
   * @constructor
   */
  constructor(parent) {

    super(null);

    /**
     * Parent ref
     * @type {Object}
     */
    this.parent = parent;

    /**
     * Texture
     * @type {Object}
     */
    this.texture = null;

    /**
     * GL texture
     * @type {Object}
     */
    this.glTexture = null;

    /**
     * Splitted sprites
     * @type {Array}
     */
    this.sprites = [];

    this.scale.set(0, 0);

    this.init();

  }

  /**
   * Initialise
   * Build shadow
   */
  init() {

    this.texture = this.buildShadow();

  }

  /**
   * Build shadow
   * @return {Object}
   */
  buildShadow() {

    let ii = 0;
    let length = 0;

    let buffer = null;

    let parent = this.parent.texture;

    let width  = 0;
    let height = 0;

    length = parent.sprites.length;

    for (; ii < length; ++ii) {
      width  = parent.sprites[ii].canvas.width;
      height = parent.sprites[ii].canvas.height;
      buffer = createCanvasBuffer(width, height);
      buffer.translate(0, height);
      buffer.scale(1, -1);
      this.drawShadow(
        parent.sprites[ii],
        buffer,
        width, height
      );
      buffer.setTransform(1, 0, 0, 1, 0, 0);
      this.sprites[ii] = buffer;
      buffer = null;
    };

    return (this);

  }

  /**
   * Create shadow of a sprite
   * @param {Object} buffer
   * @param {Object} ctx
   * @param {Number} width
   * @param {Number} height
   */
  drawShadow(buffer, ctx, width, height) {

    ctx.clear();

    ctx.drawImage(
      buffer.canvas,
      0, 0,
      width, height
    );

    this.drawTint(
      ctx,
      0, 0,
      width, height,
      SHADOW_ALPHA * 1e2
    );

  }

  /**
   * Tint a canvas
   * @param {Object} ctx
   * @param {Number} x
   * @param {Number} y
   * @param {Number} width
   * @param {Number} height
   * @param {Number} value
   */
  drawTint(ctx, x, y, width, height, value) {

    let imgData = ctx.getImageData(
      x, y,
      width, height
    );

    colorizePixels(
      imgData,
      0,
      0,
      value,
      true
    );

    ctx.putImageData(
      imgData,
      x, y
    );

  }

}

import {
  getTime
} from "../utils";

/**
 * Draw time based lightning
 * @param {Object} buffer
 * @param {Object} ctx
 * @param {Number} x
 * @param {Number} y
 * @param {Number} width
 * @param {Number} height
 * @param {Array}  colors
 */
export function drawTimeLightning(buffer, ctx, x, y, width, height, colors) {

  let hour = 18/*getTime().hours*/;

  let imgData = buffer.getImageData(
    x, y,
    width, height
  );

  this.colorizePixels(
    imgData,
    colors[hour][0] / 100,
    colors[hour][1] / 100,
    colors[hour][2] / 100,
    false
  );

  ctx.putImageData(
    imgData,
    x, y
  );

  return void 0;

};

/**
 * Colorize pixels
 * @param {Object} imgData
 * @param {Number}  r
 * @param {Number}  g
 * @param {Number}  b
 * @param {Boolean} strict
 */
export function colorizePixels(imgData, r, g, b, strict) {

  let ii = 0;
  let length = 0;

  let pixels = imgData.data;

  length = pixels.length;

  if (strict) {
    for (; ii < length / 4; ++ii) {
      if (pixels[ii * 4] > 0) {
        pixels[ii * 4] = g;
      }
      if (pixels[ii * 4 + 1] > 0) {
        pixels[ii * 4 + 1] = r;
      }
      if (pixels[ii * 4 + 2] > 0) {
        pixels[ii * 4 + 2] = g;
      }
      if (pixels[ii * 4 + 3] > 2) {
        pixels[ii * 4 + 3] = b;
      }
    };
  } else {
    for (; ii < length / 4; ++ii) {
      pixels[ii * 4 + 1] = pixels[ii * 4 + 1] / r;
      pixels[ii * 4 + 2] = pixels[ii * 4 + 2] / g;
      pixels[ii * 4 + 3] = pixels[ii * 4 + 3] * b;
    };
  }

  return void 0;

}

import {
  DEV_MODE,
  ColorPalette
} from "../../cfg";

import {
  inherit,
  TextureCache,
  createCanvasBuffer,
  imageToCanvas,
  antiCache
} from "../utils";

import * as effect from "./effects";

/**
 * Texture
 * @class Texture
 * @export
 */
export default class Texture {

  /**
   * @param {String}   url
   * @param {Number}   width
   * @param {Number}   height
   * @param {Function} resolve
   * @constructor
   */
  constructor(url, width, height, resolve) {

    /**
     * Texture
     * @type {Object}
     */
    this.texture = null;

    /**
     * Texture effect
     * @type {Object}
     */
    this.texture_effect = null;

    /**
     * Effect texture
     * @type {Object}
     */
    this.effect_sprites = [];

    /**
     * Splitted sprites
     * @type {Array}
     */
    this.sprites = [];

    /**
     * Image url
     * @type {String}
     */
    this.imgUrl = url;

    /**
     * Width
     * @type {Number}
     */
    this.width = 0;

    /**
     * Height
     * @type {Number}
     */
    this.height = 0;

    /**
     * Sprite width
     * @type {Number}
     */
    this.sWidth = width;

    /**
     * Sprite height
     * @type {Number}
     */
    this.sHeight = height;

    /**
     * X multiplicator
     * @type {Number}
     */
    this.xMul = 0;

    /**
     * Y multiplicator
     * @type {Number}
     */
    this.yMul = 0;

    /**
     * Loading state
     * @type {Boolean}
     */
    this.hasLoaded = false;

    this.fromImage(this.imgUrl, this::function() {
      resolve(this);
    });

  }

  /**
   * @param {String}   url
   * @param {Function} resolve
   */
  fromImage(url, resolve) {

    let img = null;

    let texture = TextureCache[url];

    if (
      texture !== void 0 &&
      texture instanceof Texture
    ) {
      this.hasLoaded = true;
      return (TextureCache[url]);
    }

    img = new Image();

    img.addEventListener('load', this::function() {
      this.width  = img.width;
      this.height = img.height;
      this.hasLoaded = true;
      this.texture = imageToCanvas(img);
      this.splitTexture();
      TextureCache[url] = this;
      this.renderEffects();
      resolve();
    });

    if (DEV_MODE === true) {
      img.src = url + antiCache();
    } else {
      img.src = url;
    }

    return void 0;

  }

  /**
   * Split texture into seperate sprites
   */
  splitTexture() {

    if (this.sWidth === -1 && this.sHeight === -1) {
      this.sWidth = this.width / 2;
      this.sHeight = this.height / 2;
    }

    this.xMul = this.height / (this.sWidth * 2);
    this.yMul = this.width / (this.sHeight * 2);

    let buffer = null;

    let ii = 0;

    let xx = 0;
    let yy = 0;

    let width  = this.width / (this.sWidth * 2);
    let height = this.height / (this.sHeight * 2);

    for (; yy < height;) {
      for (xx = 0; xx < width; ++xx) {
        if (xx === 0) ++yy;
        buffer = createCanvasBuffer(this.sWidth * 2, this.sHeight * 2);
        buffer.drawImage(
          this.texture.canvas,
          (this.sWidth * 2) * xx, (this.sHeight * 2) * (yy - 1),
          this.width, this.height,
          0, 0,
          this.width, this.height
        );
        this.sprites.push(buffer);
        buffer = null;
      };
    };

  }

  /**
   * Render texture effects
   */
  renderEffects() {
    this.buildTimeLightning();
  }

  /**
   * Build texture time lightning
   */
  buildTimeLightning() {

    let ii = 0;
    let length = 0;

    let buffer = null;

    let width  = 0;
    let height = 0;

    length = this.sprites.length;

    for (; ii < length; ++ii) {
      width  = this.sprites[ii].canvas.width;
      height = this.sprites[ii].canvas.height;
      buffer = createCanvasBuffer(width, height);
      buffer.translate(0, height);
      buffer.scale(1, -1);
      this.drawTimeLightning(
        this.sprites[ii],
        buffer,
        0, 0,
        width, height,
        ColorPalette
      );
      buffer.setTransform(1, 0, 0, 1, 0, 0);
      this.effect_sprites[ii] = buffer;
      buffer = null;
    };

  }

}

