/**
 * Update entity noise
 * @param {Object} entity
 * @param {Object} distance
 */
export function updateEntityNoise(entity, dist) {

  let radius = 0;

  let cx = 0;
  let cy = 0;
  let dx = 0;
  let dy = 0;

  if (BGS === false) {
    dist.x = 99999;
    dist.y = 99999;
    radius = 0;
    if (entity.noise._audioNode !== void 0) {
      entity.noise._audioNode[0].gain.value = 0;
