  radius = (entity.noiseRadius - DIMENSION) || DIMENSION;
  cx = radius / 2;
  cy = radius / 2;
  dx = Math.floor(dist.x * 1e2) + cx;
  dy = Math.floor(dist.y * 1e2) + cy;

  if (entity.STATES.NOISE === false) {
