        this.engine.camera.focus(this.engine.getEntityByProperty("Felix", "name"), true);
        this.setup(stage);
      return void 0;
      case 7:
        this.input = new Input(Events, this);
        this.setup(stage);
      return void 0;
      case 8:
        if (!OFFLINE_MODE) {
          this.engine.connection = new Connection(
            this,
            `${CONNECTION_URL}:${CONNECTION_PORT}`
          );
        }
        this.setup(stage);
      return void 0;
    };

    return void 0;

  }

